import{LitElement,html,css}from"../lit-element/lit-element.js";import{DileSpinnerMixin}from"./dile-spinner-mixin.js";import"./dile-spinner.js";class DileSpinnerModal extends DileSpinnerMixin(LitElement){static get styles(){return css`
      :host {
        display: block;
      }
      div {
        display: flex;
        position: fixed;
        background-color: var(--dile-spinner-modal-background-color, rgba(255, 255, 255, 0.8));
        height: 100vh;
        width: 100vw;
        justify-content: center;
        align-items: center;
        top: 0;
        left: 0;
        z-index: 999999999999999;
      }
      blockquote {
        width: 120px;
        height: 120px;
        border-radius: 60px;
        background-color: var(--dile-spinner-modal-box-color, rgba(0, 0, 0, 0.9));
        display: flex;
        align-items: center;
        justify-content: center;
      }

      #main {
        height:40px;
        width:40px;
        background:indianred;
        border-radius:100%;
        top:0;
        left:0;
        right:0;
        bottom:0;
        z-index:100;
      }
      
      div.main {
        background:#fff;
        height:14px;
        width:14px;
        border-radius:100%;
        position:absolute;
        margin:auto;
      }
      
      .ball-one {
        top:0;
        left:0;
        right:65px;
        bottom:65px;
        animation: anim 2s infinite ease-in;
      }
      
      @keyframes anim {
        0%{
          background:skyblue;
          right:65px;
          bottom:65px;
          z-index:150;
        }
        33%{
          right:-65px;
          bottom:-65px;
        }
        66%{
          width:10px;
          height:10px;
        }
        99%{
          right:65px;
          bottom:65px;
          height:14px;
          width:14px;
          z-index:0;
        }
         100%{
          background:skyblue;
        }
      }
      
      .ball-two {
        top:0;
        right:0;
        left:65px;
        bottom:65px;
        animation: anim-two 2s infinite ease-in;
        animation-delay: .4s;
      }
      
      @keyframes anim-two {
        0%{
          background:skyblue;
          left:65px;
          bottom:65px;
          z-index:150;
        }
        33%{
          left:-65px;
          bottom:-65px;
        }
        66%{
          width:10px;
          height:10px;
        }
        99%{
          left:65px;
          bottom:65px;
          height:14px;
          width:14px;
          z-index:0;
        }
        100%{
          background:skyblue;
        }
      }
      
      .ball-three {
        top:-65px;
        right:0;
        left:0;
        bottom:0;
        animation: anim-three 2s infinite ease-in;
        animation-delay: .8s;
      }
      
      @keyframes anim-three {
        0%{
          background:skyblue;
          top:-65px;
          z-index:150;
        }
        33%{
          top:65px;
        }
        66%{
          width:10px;
          height:10px;
        }
        99%{
          top:-65px;
          height:14px;
          width:14px;
          z-index:0;
        }
         100%{
          background:skyblue;
        }
      }         
    `}render(){return html`
      ${this.active?html`
        <style>
        html,body{
          overflow:hidden;
        }
        
 
        </style>
          <div>
            <blockquote class="box">
               <dile-spinner ?active="${this.active}">
              <div id="main">
              </div>
              <div class='ball-one'>
              </div>
              <div class='ball-two'>
              </div>
              <div class='ball-three'>
              </div>      
              </dile-spinner> 
            </blockquote>
          </div>`:""}
    `}}customElements.define("dile-spinner-modal",DileSpinnerModal);